import React from 'react'


const Header = (props) => {
  return <div>
<h1>{props.name}</h1>
  </div>;
};


const Part = (props) => {
  return <div>
    <p>{props.part} {props.exercises}</p>
  </div>
}



const Content = (props) => {
  return <div>
    <Part part={props.parts[0].name} exercises={props.parts[0].exercise}/>
  </div>;
};


const Total = (props) => {
  let sum = props.parts.reduce((prev, current) => {
    return prev + +current.exercises
  }, 0);

  return <div>
    <p> Number of exercises is {sum}</p>
  </div>;
};

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]


  return (
    <div>
      <Header name={course} />
      <Content parts={parts} />
      <Total parts={parts}/>
    </div>
  )
}

export default App
