import React from 'react'


const Header = (props) => {
  return <div>
<h1>{props.name}</h1>
  </div>;
};




const Content = (props) => {
  return <div>
      <p>{props.part} {props.exercise}</p>
  </div>;
};






const Total = (props) => {
  return <div>
    <p>Number of exercises {props.a + props.b + props.c}</p>
  </div>;
};

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  console.log(typeof exercises3)

  return (
    <div>
      <Header name={course} />
      <Content part1={part1} part2={part2} part3={part3} exercise1={exercises1} exercise2={exercises2} exercise3={exercises3}/>
      <Total a={exercises1} b={exercises2} c={exercises3}/>
    </div>
  )
}

export default App
