import React from 'react'


const Header = (props) => {
  return <div>
<h1>{props.name}</h1>
  </div>;
};


const Part = (props) => {
  return <div>
    <p>{props.name} {props.exercise}</p>
  </div>;
};



const Content = (props) => {
  return <div>
      <Part part1={props.part1} exercise1={props.exercise1}/>
      <Part part2={props.part2} exercise2={props.exercise2}/>
      <Part part3={props.part3} exercise3={props.exercise3}/>
  </div>;
};






const Total = (props) => {
  return <div>
    <p>Number of exercises {props.a + props.b + props.c}</p>
  </div>;
};

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  console.log(typeof exercises3)

  return (
    <div>
      <Header name={course} />
      <Content />
      <Total a={exercises1} b={exercises2} c={exercises3}/>
    </div>
  )
}

export default App
